const moduleButton=document.querySelector(".module__button"),popup=document.querySelector(".alert"),alertOverlay=document.querySelector(".alert-overlay"),closeButton=document.querySelector(".alert__close");moduleButton.addEventListener("click",e=>{alertOverlay.classList.toggle("alert-overlay--hide"),popup.classList.toggle("alert--hide")}),closeButton.addEventListener("click",e=>{alertOverlay.classList.toggle("alert-overlay--hide"),popup.classList.toggle("alert--hide")});